import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { z } from "zod";
import { storage } from "./storage";
import { bulkJobProcessor } from "./services/bulk-jobs";
import { csvExportService } from "./services/csv-export";
import { authService } from "./services/auth";
import { createShopifyService } from "./services/shopify";
import { logger } from "./utils/logger";
import { 
  generalApiLimiter, 
  bulkOperationLimiter, 
  exportLimiter, 
  shopifyApiLimiter 
} from "./middleware/rate-limit";

export async function registerRoutes(app: Express): Promise<Server> {
  // Apply general rate limiting to all API routes
  app.use('/api', generalApiLimiter.middleware());

  // Middleware to log API calls
  app.use('/api', async (req, res, next) => {
    const start = Date.now();

    res.on('finish', async () => {
      const responseTime = Date.now() - start;

      try {
        await storage.createApiLog({
          endpoint: req.path,
          method: req.method,
          statusCode: res.statusCode,
          responseTime,
          shopDomain: req.headers['x-shop-domain'] as string || req.body?.shopDomain,
          errorMessage: res.statusCode >= 400 ? res.statusMessage : undefined,
        });
      } catch (error) {
        logger.error('Failed to log API call', {
          error: error instanceof Error ? error.message : 'Unknown error',
        });
      }
    });

    next();
  });

  // Health check endpoint
  app.get('/api/health', async (req, res) => {
    try {
      const stats = await storage.getSystemStats();
      res.json({
        status: 'healthy',
        timestamp: new Date().toISOString(),
        stats,
      });
    } catch (error) {
      logger.error('Health check failed', {
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      res.status(500).json({
        status: 'unhealthy',
        error: 'Database connection failed',
      });
    }
  });

  // Get distinct vendor list (cached)
  app.get('/api/vendors', shopifyApiLimiter.middleware(), async (req, res) => {
    try {
      const { shopDomain } = req.query;

      if (!shopDomain || typeof shopDomain !== 'string') {
        return res.status(400).json({ error: 'shopDomain is required' });
      }

      // Get cached vendors from database
      let vendors = await storage.getVendors(shopDomain);

      // If no vendors in cache or cache is old, refresh from Shopify
      if (vendors.length === 0) {
        const shopSettings = await storage.getShopSettings(shopDomain);

        if (!shopSettings?.accessToken) {
          return res.status(401).json({ error: 'Shop not authenticated' });
        }

        const shopifyService = createShopifyService(shopDomain, shopSettings.accessToken);
        const shopifyVendors = await shopifyService.getAllVendors();

        // Update vendor cache
        for (const vendorName of shopifyVendors) {
          const existingVendor = await storage.getVendorByName(shopDomain, vendorName);

          if (!existingVendor) {
            await storage.createVendor({
              shopDomain,
              name: vendorName,
              productCount: 0,
            });
          }
        }

        vendors = await storage.getVendors(shopDomain);
      }

      res.json({ vendors: vendors.map(v => v.name) });
    } catch (error) {
      logger.error('Failed to get vendors', {
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      res.status(500).json({ error: 'Failed to fetch vendors' });
    }
  });

  // Bulk update vendor
  app.post('/api/bulk-update-vendor', bulkOperationLimiter.middleware(), async (req, res) => {
    try {
      const schema = z.object({
        shopDomain: z.string(),
        productIds: z.array(z.string()).min(1),
        vendor: z.string().min(1),
      });

      const { shopDomain, productIds, vendor } = schema.parse(req.body);

      // Validate shop access
      const hasAccess = await authService.validateShopAccess(shopDomain);
      if (!hasAccess) {
        return res.status(401).json({ error: 'Shop not authenticated' });
      }

      // Create bulk job
      const job = await bulkJobProcessor.createJob({
        shopDomain,
        productIds,
        targetVendor: vendor,
        status: 'PENDING',
        totalCount: productIds.length,
      });

      logger.bulkJobStart(job.id, shopDomain, productIds.length);

      res.json({
        jobId: job.id,
        status: job.status,
        message: 'Bulk update job created successfully',
      });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({
          error: 'Invalid request data',
          details: error.errors,
        });
      }

      logger.error('Bulk update failed', {
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      res.status(500).json({ error: 'Failed to create bulk update job' });
    }
  });

  // Get bulk job status
  app.get('/api/bulk-jobs/:id', async (req, res) => {
    try {
      const { id } = req.params;
      const jobStatus = await bulkJobProcessor.getJobStatus(id);

      if (!jobStatus.job) {
        return res.status(404).json({ error: 'Job not found' });
      }

      res.json({
        job: jobStatus.job,
        progress: jobStatus.progress,
        isProcessing: jobStatus.isProcessing,
      });
    } catch (error) {
      logger.error('Failed to get job status', {
        jobId: req.params.id,
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      res.status(500).json({ error: 'Failed to get job status' });
    }
  });

  // Get recent bulk jobs
  app.get('/api/bulk-jobs', async (req, res) => {
    try {
      const { shopDomain, limit } = req.query;

      const jobs = await bulkJobProcessor.getRecentJobs(
        shopDomain as string,
        limit ? parseInt(limit as string) : undefined
      );

      res.json({ jobs });
    } catch (error) {
      logger.error('Failed to get bulk jobs', {
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      res.status(500).json({ error: 'Failed to fetch bulk jobs' });
    }
  });

  // Retry bulk job
  app.post('/api/bulk-jobs/:id/retry', async (req, res) => {
    try {
      const { id } = req.params;
      const job = await bulkJobProcessor.retryJob(id);

      res.json({
        job,
        message: 'Job retry initiated',
      });
    } catch (error) {
      logger.error('Failed to retry job', {
        jobId: req.params.id,
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      res.status(400).json({ 
        error: error instanceof Error ? error.message : 'Failed to retry job',
      });
    }
  });

  // Cancel bulk job
  app.post('/api/bulk-jobs/:id/cancel', async (req, res) => {
    try {
      const { id } = req.params;
      const job = await bulkJobProcessor.cancelJob(id);

      res.json({
        job,
        message: 'Job cancelled successfully',
      });
    } catch (error) {
      logger.error('Failed to cancel job', {
        jobId: req.params.id,
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      res.status(400).json({ 
        error: error instanceof Error ? error.message : 'Failed to cancel job',
      });
    }
  });

  // Export products
  app.post('/api/export', exportLimiter.middleware(), async (req, res) => {
    try {
      const schema = z.object({
        shopDomain: z.string(),
        vendor: z.string().optional(),
        filters: z.object({
          status: z.string().optional(),
          productType: z.string().optional(),
        }).optional(),
      });

      const { shopDomain, vendor, filters } = schema.parse(req.body);

      // Validate shop access
      const hasAccess = await authService.validateShopAccess(shopDomain);
      if (!hasAccess) {
        return res.status(401).json({ error: 'Shop not authenticated' });
      }

      logger.exportRequest(shopDomain, vendor, filters);

      const signedUrl = await csvExportService.exportProducts({
        shopDomain,
        vendor,
        filters,
      });

      res.json({
        downloadUrl: signedUrl,
        message: 'Export created successfully',
      });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({
          error: 'Invalid request data',
          details: error.errors,
        });
      }

      logger.error('Export failed', {
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      res.status(500).json({ error: 'Failed to create export' });
    }
  });

  // Download exported CSV
  app.get('/api/export/download/:exportId', async (req, res) => {
    try {
      const { exportId } = req.params;
      const { token } = req.query;

      if (!token || typeof token !== 'string') {
        return res.status(400).json({ error: 'Missing export token' });
      }

      // Verify token
      const tokenData = csvExportService.verifyExportToken(token);

      // Get CSV content
      const csvContent = await csvExportService.getCsvContent(exportId);

      if (!csvContent) {
        return res.status(404).json({ error: 'Export not found or expired' });
      }

      // Set headers for CSV download
      res.setHeader('Content-Type', 'text/csv');
      res.setHeader('Content-Disposition', `attachment; filename="products-export-${exportId}.csv"`);
      res.send(csvContent);

      logger.info('CSV download completed', {
        exportId,
        shopDomain: tokenData.shopDomain,
      });
    } catch (error) {
      logger.error('CSV download failed', {
        exportId: req.params.exportId,
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      res.status(400).json({ error: 'Invalid or expired download link' });
    }
  });

  // Get shop settings
  app.get('/api/settings', async (req, res) => {
    try {
      const { shopDomain } = req.query;

      if (!shopDomain || typeof shopDomain !== 'string') {
        return res.status(400).json({ error: 'shopDomain is required' });
      }

      const settings = await storage.getShopSettings(shopDomain);

      if (!settings) {
        return res.status(404).json({ error: 'Shop settings not found' });
      }

      // Return settings without sensitive data
      res.json({
        shopDomain: settings.shopDomain,
        showVendorColumn: settings.showVendorColumn,
        lastUpdated: settings.lastUpdated,
      });
    } catch (error) {
      logger.error('Failed to get settings', {
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      res.status(500).json({ error: 'Failed to fetch settings' });
    }
  });

  // Update shop settings
  app.post('/api/settings', async (req, res) => {
    try {
      const schema = z.object({
        shopDomain: z.string(),
        showVendorColumn: z.boolean().optional(),
      });

      const { shopDomain, showVendorColumn } = schema.parse(req.body);

      // Validate shop access
      const hasAccess = await authService.validateShopAccess(shopDomain);
      if (!hasAccess) {
        return res.status(401).json({ error: 'Shop not authenticated' });
      }

      const updatedSettings = await storage.updateShopSettings(shopDomain, {
        showVendorColumn,
      });

      res.json({
        shopDomain: updatedSettings.shopDomain,
        showVendorColumn: updatedSettings.showVendorColumn,
        lastUpdated: updatedSettings.lastUpdated,
      });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({
          error: 'Invalid request data',
          details: error.errors,
        });
      }

      logger.error('Failed to update settings', {
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      res.status(500).json({ error: 'Failed to update settings' });
    }
  });

  // Get system statistics
  app.get('/api/stats', async (req, res) => {
    try {
      const stats = await storage.getSystemStats();
      const apiStats = await storage.getApiLogStats(24);

      res.json({
        ...stats,
        apiEndpoints: apiStats,
      });
    } catch (error) {
      logger.error('Failed to get system stats', {
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      res.status(500).json({ error: 'Failed to fetch system statistics' });
    }
  });

  // Get API logs
  app.get('/api/logs', async (req, res) => {
    try {
      const { shopDomain, limit } = req.query;

      const logs = await storage.getApiLogs(
        shopDomain as string,
        limit ? parseInt(limit as string) : undefined
      );

      res.json({ logs });
    } catch (error) {
      logger.error('Failed to get API logs', {
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      res.status(500).json({ error: 'Failed to fetch API logs' });
    }
  });

  // OAuth callback endpoint (for Shopify app installation)
  app.get('/api/auth/callback', async (req, res) => {
    try {
      const { code, shop, state } = req.query;

      if (!code || !shop) {
        return res.status(400).json({ error: 'Missing required parameters' });
      }

      // In production, validate state parameter for CSRF protection

      const clientId = process.env.SHOPIFY_CLIENT_ID || 'your_client_id';
      const clientSecret = process.env.SHOPIFY_CLIENT_SECRET || 'your_client_secret';

      const tokenData = await authService.exchangeCodeForToken(
        shop as string,
        code as string,
        clientId,
        clientSecret
      );

      await authService.storeShopCredentials(
        shop as string,
        tokenData.access_token,
        tokenData.scope
      );

      logger.authSuccess(shop as string, tokenData.scope);

      res.redirect(`/dashboard?shop=${shop}&installed=true`);
    } catch (error) {
      logger.authFailure(
        req.query.shop as string || 'unknown',
        error instanceof Error ? error.message : 'Unknown error'
      );
      res.status(400).json({ error: 'Authentication failed' });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, shopSettings, bulkJobs, vendors, apiLogs, systemMetrics,
  type User, type InsertUser,
  type ShopSettings, type InsertShopSettings,
  type BulkJob, type InsertBulkJob,
  type Vendor, type InsertVendor,
  type ApiLog, type InsertApiLog,
  type SystemMetric, type InsertSystemMetric
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, gte, count, sql } from "drizzle-orm";

export interface IStorage {
  // User methods
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Shop settings methods
  getShopSettings(shopDomain: string): Promise<ShopSettings | undefined>;
  createShopSettings(settings: InsertShopSettings): Promise<ShopSettings>;
  updateShopSettings(shopDomain: string, settings: Partial<InsertShopSettings>): Promise<ShopSettings>;

  // Bulk job methods
  createBulkJob(job: InsertBulkJob): Promise<BulkJob>;
  getBulkJob(id: string): Promise<BulkJob | undefined>;
  updateBulkJob(id: string, updates: Partial<BulkJob>): Promise<BulkJob>;
  getRecentBulkJobs(shopDomain?: string, limit?: number): Promise<BulkJob[]>;
  getBulkJobsByStatus(status: BulkJob['status']): Promise<BulkJob[]>;

  // Vendor methods
  getVendors(shopDomain: string): Promise<Vendor[]>;
  createVendor(vendor: InsertVendor): Promise<Vendor>;
  updateVendor(id: string, updates: Partial<Vendor>): Promise<Vendor>;
  deleteVendor(id: string): Promise<void>;
  getVendorByName(shopDomain: string, name: string): Promise<Vendor | undefined>;

  // API log methods
  createApiLog(log: InsertApiLog): Promise<ApiLog>;
  getApiLogs(shopDomain?: string, limit?: number): Promise<ApiLog[]>;
  getApiLogStats(hours?: number): Promise<{ endpoint: string; avgResponseTime: number; errorRate: number }[]>;

  // System metrics methods
  createSystemMetric(metric: InsertSystemMetric): Promise<SystemMetric>;
  getSystemMetrics(metricName?: string, hours?: number): Promise<SystemMetric[]>;
  getSystemStats(): Promise<{
    activeShops: number;
    totalVendors: number;
    bulkJobsToday: number;
    apiSuccessRate: number;
  }>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async getShopSettings(shopDomain: string): Promise<ShopSettings | undefined> {
    const [settings] = await db
      .select()
      .from(shopSettings)
      .where(eq(shopSettings.shopDomain, shopDomain));
    return settings || undefined;
  }

  async createShopSettings(settings: InsertShopSettings): Promise<ShopSettings> {
    const [newSettings] = await db
      .insert(shopSettings)
      .values(settings)
      .returning();
    return newSettings;
  }

  async updateShopSettings(shopDomain: string, updates: Partial<InsertShopSettings>): Promise<ShopSettings> {
    const [updatedSettings] = await db
      .update(shopSettings)
      .set({ ...updates, lastUpdated: new Date() })
      .where(eq(shopSettings.shopDomain, shopDomain))
      .returning();
    return updatedSettings;
  }

  async createBulkJob(job: InsertBulkJob): Promise<BulkJob> {
    const jobData = {
      shopDomain: job.shopDomain,
      productIds: job.productIds,
      targetVendor: job.targetVendor,
      status: job.status || ('PENDING' as const),
      totalCount: job.productIds.length,
    };

    const [newJob] = await db
      .insert(bulkJobs)
      .values(jobData)
      .returning();
    return newJob;
  }

  async getBulkJob(id: string): Promise<BulkJob | undefined> {
    const [job] = await db
      .select()
      .from(bulkJobs)
      .where(eq(bulkJobs.id, id));
    return job || undefined;
  }

  async updateBulkJob(id: string, updates: Partial<BulkJob>): Promise<BulkJob> {
    const [updatedJob] = await db
      .update(bulkJobs)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(bulkJobs.id, id))
      .returning();
    return updatedJob;
  }

  async getRecentBulkJobs(shopDomain?: string, limit = 10): Promise<BulkJob[]> {
    if (shopDomain) {
      return await db
        .select()
        .from(bulkJobs)
        .where(eq(bulkJobs.shopDomain, shopDomain))
        .orderBy(desc(bulkJobs.createdAt))
        .limit(limit);
    }

    return await db
      .select()
      .from(bulkJobs)
      .orderBy(desc(bulkJobs.createdAt))
      .limit(limit);
  }

  async getBulkJobsByStatus(status: BulkJob['status']): Promise<BulkJob[]> {
    return await db
      .select()
      .from(bulkJobs)
      .where(eq(bulkJobs.status, status as any))
      .orderBy(desc(bulkJobs.createdAt));
  }

  async getVendors(shopDomain: string): Promise<Vendor[]> {
    return db
      .select()
      .from(vendors)
      .where(eq(vendors.shopDomain, shopDomain))
      .orderBy(vendors.name);
  }

  async createVendor(vendor: InsertVendor): Promise<Vendor> {
    const [newVendor] = await db
      .insert(vendors)
      .values(vendor)
      .returning();
    return newVendor;
  }

  async updateVendor(id: string, updates: Partial<Vendor>): Promise<Vendor> {
    const [updatedVendor] = await db
      .update(vendors)
      .set({ ...updates, lastUpdated: new Date() })
      .where(eq(vendors.id, id))
      .returning();
    return updatedVendor;
  }

  async deleteVendor(id: string): Promise<void> {
    await db.delete(vendors).where(eq(vendors.id, id));
  }

  async getVendorByName(shopDomain: string, name: string): Promise<Vendor | undefined> {
    const [vendor] = await db
      .select()
      .from(vendors)
      .where(and(eq(vendors.shopDomain, shopDomain), eq(vendors.name, name)));
    return vendor || undefined;
  }

  async createApiLog(log: InsertApiLog): Promise<ApiLog> {
    const [newLog] = await db
      .insert(apiLogs)
      .values(log)
      .returning();
    return newLog;
  }

  async getApiLogs(shopDomain?: string, limit = 100): Promise<ApiLog[]> {
    if (shopDomain) {
      return await db
        .select()
        .from(apiLogs)
        .where(eq(apiLogs.shopDomain, shopDomain))
        .orderBy(desc(apiLogs.timestamp))
        .limit(limit);
    }

    return await db
      .select()
      .from(apiLogs)
      .orderBy(desc(apiLogs.timestamp))
      .limit(limit);
  }

  async getApiLogStats(hours = 24): Promise<{ endpoint: string; avgResponseTime: number; errorRate: number }[]> {
    const since = new Date(Date.now() - hours * 60 * 60 * 1000);

    const stats = await db
      .select({
        endpoint: apiLogs.endpoint,
        avgResponseTime: sql<number>`AVG(${apiLogs.responseTime})::integer`,
        totalRequests: count(),
        errorCount: sql<number>`SUM(CASE WHEN ${apiLogs.statusCode} >= 400 THEN 1 ELSE 0 END)::integer`,
      })
      .from(apiLogs)
      .where(gte(apiLogs.timestamp, since))
      .groupBy(apiLogs.endpoint);

    return stats.map(stat => ({
      endpoint: stat.endpoint,
      avgResponseTime: stat.avgResponseTime || 0,
      errorRate: stat.totalRequests > 0 ? (stat.errorCount / stat.totalRequests) * 100 : 0,
    }));
  }

  async createSystemMetric(metric: InsertSystemMetric): Promise<SystemMetric> {
    const [newMetric] = await db
      .insert(systemMetrics)
      .values(metric)
      .returning();
    return newMetric;
  }

  async getSystemMetrics(metricName?: string, hours = 24): Promise<SystemMetric[]> {
    const since = new Date(Date.now() - hours * 60 * 60 * 1000);

    if (metricName) {
      return await db
        .select()
        .from(systemMetrics)
        .where(and(
          gte(systemMetrics.timestamp, since),
          eq(systemMetrics.metricName, metricName)
        ))
        .orderBy(desc(systemMetrics.timestamp));
    }

    return await db
      .select()
      .from(systemMetrics)
      .where(gte(systemMetrics.timestamp, since))
      .orderBy(desc(systemMetrics.timestamp));
  }

  async getSystemStats(): Promise<{
    activeShops: number;
    totalVendors: number;
    bulkJobsToday: number;
    apiSuccessRate: number;
  }> {
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const [activeShopsResult] = await db
      .select({ count: count() })
      .from(shopSettings);

    const [totalVendorsResult] = await db
      .select({ count: count() })
      .from(vendors);

    const [bulkJobsTodayResult] = await db
      .select({ count: count() })
      .from(bulkJobs)
      .where(gte(bulkJobs.createdAt, today));

    const [apiStatsResult] = await db
      .select({
        total: count(),
        errors: sql<number>`SUM(CASE WHEN ${apiLogs.statusCode} >= 400 THEN 1 ELSE 0 END)::integer`,
      })
      .from(apiLogs)
      .where(gte(apiLogs.timestamp, today));

    const successRate = apiStatsResult.total > 0 
      ? ((apiStatsResult.total - apiStatsResult.errors) / apiStatsResult.total) * 100 
      : 100;

    return {
      activeShops: activeShopsResult.count,
      totalVendors: totalVendorsResult.count,
      bulkJobsToday: bulkJobsTodayResult.count,
      apiSuccessRate: Math.round(successRate * 10) / 10,
    };
  }
}

export const storage = new DatabaseStorage();

