import { createShopifyService } from './shopify';
import { storage } from '../storage';
import { logger } from '../utils/logger';
import jwt from 'jsonwebtoken';
import type { ShopifyProduct } from './shopify';

export interface ExportOptions {
  shopDomain: string;
  vendor?: string;
  filters?: {
    status?: string;
    productType?: string;
  };
}

export class CSVExportService {
  private readonly JWT_SECRET: string;
  private readonly EXPORT_EXPIRY = '1h'; // 1 hour expiry for signed URLs
  private readonly MOCK_MODE = process.env.MOCK_SHOPIFY === 'true';

  constructor() {
    // Critical security fix: Require SESSION_SECRET, no insecure fallback
    if (!process.env.SESSION_SECRET) {
      throw new Error('SESSION_SECRET environment variable is required for secure JWT signing');
    }
    this.JWT_SECRET = process.env.SESSION_SECRET;
  }

  async exportProducts(options: ExportOptions): Promise<string> {
    try {
      logger.info('Starting CSV export', options);

      let products: ShopifyProduct[] = [];
      let isUsingMockData = false;

      try {
        // Get shop settings
        const shopSettings = await storage.getShopSettings(options.shopDomain);
        if (!shopSettings?.accessToken) {
          throw new Error('Shop access token not found');
        }

        const shopifyService = createShopifyService(options.shopDomain, shopSettings.accessToken);

        if (options.vendor) {
          // Get products by specific vendor
          products = await shopifyService.getProductsByVendor(options.vendor);
        } else {
          // Get all products
          let hasNextPage = true;
          let cursor: string | undefined;

          while (hasNextPage) {
            const response = await shopifyService.getProducts(250, cursor);

            response.products.edges.forEach(edge => {
              products.push(edge.node);
            });

            hasNextPage = response.products.pageInfo.hasNextPage;
            cursor = response.products.pageInfo.endCursor;
          }
        }
      } catch (shopifyError) {
        // Check if it's a 401 error or if we're in mock mode
        const is401Error = shopifyError instanceof Error && shopifyError.message.includes('401');
        const shouldUseMockData = this.MOCK_MODE || is401Error;

        if (shouldUseMockData) {
          logger.warn('Shopify API failed, using mock data for CSV export', {
            shopDomain: options.shopDomain,
            vendor: options.vendor,
            mockMode: this.MOCK_MODE,
            is401Error,
            error: shopifyError instanceof Error ? shopifyError.message : 'Unknown error'
          });

          products = this.generateMockProducts(options.vendor);
          isUsingMockData = true;
        } else {
          throw shopifyError; // Re-throw if not in mock mode and not 401 error
        }
      }

      // Apply additional filters
      if (options.filters) {
        products = products.filter(product => {
          if (options.filters?.status && product.status !== options.filters.status) {
            return false;
          }
          if (options.filters?.productType && product.productType !== options.filters.productType) {
            return false;
          }
          return true;
        });
      }

      // Generate CSV content
      const csvContent = this.generateCSV(products, isUsingMockData);

      // Create signed URL for download
      const token = jwt.sign({
        shopDomain: options.shopDomain,
        exportDate: new Date().toISOString(),
        filters: options.filters || {},
        vendor: options.vendor,
        isMockData: isUsingMockData,
      }, this.JWT_SECRET, { expiresIn: this.EXPORT_EXPIRY });

      // Store CSV content temporarily (in production, use cloud storage)
      const exportId = this.generateExportId();
      await this.storeCsvContent(exportId, csvContent);

      const signedUrl = `/api/export/download/${exportId}?token=${token}`;

      logger.info('CSV export completed', {
        shopDomain: options.shopDomain,
        productCount: products.length,
        exportId,
        isMockData: isUsingMockData,
      });

      return signedUrl;

    } catch (error) {
      logger.error('CSV export failed', {
        shopDomain: options.shopDomain,
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      throw error;
    }
  }

  private generateCSV(products: ShopifyProduct[], isMockData = false): string {
    const headers = [
      'Product ID',
      'Title',
      'Vendor',
      'Status',
      'Product Type',
      'Total Inventory'
    ];

    // Add a note if using mock data
    const csvHeader = isMockData 
      ? '# This export contains sample/mock data for testing purposes\n' + headers.join(',')
      : headers.join(',');

    const rows = products.map(product => [
      product.id,
      `"${product.title.replace(/"/g, '""')}"`, // Escape quotes
      `"${product.vendor.replace(/"/g, '""')}"`,
      product.status,
      `"${product.productType.replace(/"/g, '""')}"`,
      product.totalInventory.toString()
    ]);

    return [csvHeader, ...rows.map(row => row.join(','))].join('\n');
  }

  private generateExportId(): string {
    return `export_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private csvStorage = new Map<string, string>();

  private async storeCsvContent(exportId: string, content: string): Promise<void> {
    // In production, this should use cloud storage (S3, GCS, etc.)
    this.csvStorage.set(exportId, content);

    // Clean up after 2 hours
    setTimeout(() => {
      this.csvStorage.delete(exportId);
    }, 2 * 60 * 60 * 1000);
  }

  async getCsvContent(exportId: string): Promise<string | undefined> {
    return this.csvStorage.get(exportId);
  }

  verifyExportToken(token: string): any {
    try {
      return jwt.verify(token, this.JWT_SECRET);
    } catch (error) {
      logger.warn('Invalid export token', {
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      throw new Error('Invalid or expired export token');
    }
  }

  async exportByVendor(shopDomain: string, vendor: string): Promise<string> {
    return this.exportProducts({
      shopDomain,
      vendor,
    });
  }

  async exportAll(shopDomain: string, filters?: ExportOptions['filters']): Promise<string> {
    return this.exportProducts({
      shopDomain,
      filters,
    });
  }

  private generateMockProducts(vendor?: string): ShopifyProduct[] {
    const mockProducts: ShopifyProduct[] = [
      {
        id: 'gid://shopify/Product/1001',
        title: 'Sample Wireless Headphones',
        vendor: vendor || 'AudioTech',
        status: 'ACTIVE',
        productType: 'Electronics',
        totalInventory: 150,
      },
      {
        id: 'gid://shopify/Product/1002',
        title: 'Eco-Friendly Water Bottle',
        vendor: vendor || 'GreenLife',
        status: 'ACTIVE',
        productType: 'Lifestyle',
        totalInventory: 75,
      },
      {
        id: 'gid://shopify/Product/1003',
        title: 'Premium Coffee Beans',
        vendor: vendor || 'RoastMaster',
        status: 'ACTIVE',
        productType: 'Food & Beverage',
        totalInventory: 200,
      },
      {
        id: 'gid://shopify/Product/1004',
        title: 'Organic Cotton T-Shirt',
        vendor: vendor || 'EcoWear',
        status: 'DRAFT',
        productType: 'Apparel',
        totalInventory: 50,
      },
      {
        id: 'gid://shopify/Product/1005',
        title: 'Bluetooth Speaker',
        vendor: vendor || 'SoundWave',
        status: 'ACTIVE',
        productType: 'Electronics',
        totalInventory: 30,
      }
    ];

    // If a specific vendor is requested, return only products for that vendor
    if (vendor) {
      return mockProducts.filter(product => product.vendor === vendor);
    }

    return mockProducts;
  }
}

export const csvExportService = new CSVExportService();

server/services/shopify.ts.content:
import { logger } from '../utils/logger';

export interface ShopifyProduct {
  id: string;
  title: string;
  vendor: string;
  status: string;
  totalInventory: number;
  productType: string;
}

export interface ShopifyProductsResponse {
  products: {
    edges: Array<{
      cursor: string;
      node: ShopifyProduct;
    }>;
    pageInfo: {
      hasNextPage: boolean;
      endCursor: string;
    };
  };
}

export interface ShopifyProductUpdateResponse {
  productUpdate: {
    product?: {
      id: string;
      vendor: string;
    };
    userErrors: Array<{
      field: string;
      message: string;
    }>;
  };
}

export interface BulkUpdateResult {
  successCount: number;
  failureCount: number;
  errors: Array<{
    productId: string;
    error: string;
  }>;
  userErrors: Array<{
    productId: string;
    field: string;
    message: string;
  }>;
}

export class ShopifyService {
  private baseUrl: string;
  private accessToken: string;

  constructor(shopDomain: string, accessToken: string) {
    this.baseUrl = `https://${shopDomain}/admin/api/2023-10/graphql.json`;
    this.accessToken = accessToken;
  }

  private async makeGraphQLRequestWithRetry<T>(query: string, variables?: any, maxRetries = 3): Promise<T> {
    let lastError: Error | null = null;

    for (let attempt = 1; attempt <= maxRetries; attempt++) {
      try {
        return await this.makeGraphQLRequest<T>(query, variables);
      } catch (error) {
        lastError = error as Error;

        // Don't retry 4xx errors except 429
        if (error instanceof Error && error.message.includes('Shopify API error:')) {
          const statusMatch = error.message.match(/Shopify API error: (\d+)/);
          if (statusMatch) {
            const status = parseInt(statusMatch[1]);
            if (status >= 400 && status < 500 && status !== 429) {
              throw error; // Don't retry 4xx except 429
            }
          }
        }

        if (attempt === maxRetries) {
          break; // Final attempt failed
        }

        // Use Retry-After header for 429 errors, otherwise exponential backoff
        let delay: number;
        const retryAfter = (error as any).retryAfter;

        if (retryAfter && typeof retryAfter === 'number') {
          // Use API-specified retry delay for rate limit errors
          delay = retryAfter;
          logger.warn('Shopify API request failed (rate limited), using Retry-After delay', {
            attempt,
            maxRetries,
            error: error instanceof Error ? error.message : 'Unknown error',
            retryAfterMs: delay,
          });
        } else {
          // Exponential backoff with jitter for other errors
          const baseDelay = Math.pow(2, attempt - 1) * 1000; // 1s, 2s, 4s
          const jitter = Math.random() * 500; // 0-500ms jitter
          delay = baseDelay + jitter;
          logger.warn('Shopify API request failed, retrying with exponential backoff', {
            attempt,
            maxRetries,
            error: error instanceof Error ? error.message : 'Unknown error',
            retryDelay: delay,
          });
        }

        await new Promise(resolve => setTimeout(resolve, delay));
      }
    }

    throw lastError || new Error('All retry attempts failed');
  }

  private async makeGraphQLRequest<T>(query: string, variables?: any): Promise<T> {
    const startTime = Date.now();

    try {
      const response = await fetch(this.baseUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-Shopify-Access-Token': this.accessToken,
        },
        body: JSON.stringify({
          query,
          variables,
        }),
      });

      const responseTime = Date.now() - startTime;

      // Check rate limiting headers
      const callLimit = response.headers.get('X-Shopify-Shop-Api-Call-Limit');
      if (callLimit) {
        const [current, max] = callLimit.split('/').map(Number);
        if (current / max > 0.8) { // Over 80% of rate limit
          logger.warn('Approaching Shopify rate limit', {
            current,
            max,
            percentage: Math.round((current / max) * 100),
          });
        }
      }

      if (!response.ok) {
        const retryAfter = response.headers.get('Retry-After');
        logger.error('Shopify API error', {
          status: response.status,
          statusText: response.statusText,
          responseTime,
          retryAfter,
        });

        // Create error with retry-after information for 429 rate limit errors
        const error = new Error(`Shopify API error: ${response.status} ${response.statusText}`);
        if (response.status === 429 && retryAfter) {
          (error as any).retryAfter = parseInt(retryAfter, 10) * 1000; // Convert seconds to milliseconds
        }
        throw error;
      }

      const data = await response.json();

      if (data.errors) {
        logger.error('Shopify GraphQL errors', { errors: data.errors, responseTime });
        throw new Error(`GraphQL errors: ${JSON.stringify(data.errors)}`);
      }

      logger.info('Shopify API success', {
        endpoint: 'graphql',
        responseTime,
        callLimit,
      });

      return data.data as T;
    } catch (error) {
      const responseTime = Date.now() - startTime;
      logger.error('Shopify API request failed', {
        error: error instanceof Error ? error.message : 'Unknown error',
        responseTime,
      });
      throw error;
    }
  }

  async getProducts(first = 50, after?: string, query?: string): Promise<ShopifyProductsResponse> {
    const graphqlQuery = `
      query GetProducts($first: Int!, $after: String, $query: String) {
        products(first: $first, after: $after, query: $query) {
          edges {
            cursor
            node {
              id
              title
              vendor
              status
              totalInventory
              productType
            }
          }
          pageInfo {
            hasNextPage
            endCursor
          }
        }
      }
    `;

    return this.makeGraphQLRequestWithRetry<ShopifyProductsResponse>(graphqlQuery, {
      first,
      after,
      query,
    });
  }

  async updateProductVendor(productId: string, vendor: string): Promise<ShopifyProductUpdateResponse> {
    const mutation = `
      mutation UpdateProductVendor($productId: ID!, $vendor: String!) {
        productUpdate(input: {id: $productId, vendor: $vendor}) {
          product {
            id
            vendor
          }
          userErrors {
            field
            message
          }
        }
      }
    `;

    return this.makeGraphQLRequestWithRetry<ShopifyProductUpdateResponse>(mutation, {
      productId,
      vendor,
    });
  }

  async bulkUpdateVendor(productIds: string[], vendor: string): Promise<BulkUpdateResult> {
    const result: BulkUpdateResult = {
      successCount: 0,
      failureCount: 0,
      errors: [],
      userErrors: [],
    };

    // Process products individually since Shopify doesn't have a bulk vendor update mutation
    for (const productId of productIds) {
      try {
        const response = await this.updateProductVendor(productId, vendor);

        if (response.productUpdate.userErrors.length > 0) {
          result.failureCount++;
          response.productUpdate.userErrors.forEach(error => {
            result.userErrors.push({
              productId,
              field: error.field,
              message: error.message,
            });
          });
          logger.warn('Product update had user errors', {
            productId,
            errors: response.productUpdate.userErrors,
          });
        } else {
          result.successCount++;
          logger.debug('Product vendor updated successfully', {
            productId,
            vendor,
          });
        }

        // Add delay between requests to respect rate limits
        if (productIds.indexOf(productId) < productIds.length - 1) {
          await new Promise(resolve => setTimeout(resolve, 250)); // 4 requests per second
        }

      } catch (error) {
        result.failureCount++;
        const errorMessage = error instanceof Error ? error.message : 'Unknown error';
        result.errors.push({
          productId,
          error: errorMessage,
        });
        logger.error('Product update failed', {
          productId,
          error: errorMessage,
        });
      }
    }

    logger.info('Bulk vendor update completed', {
      totalProducts: productIds.length,
      successCount: result.successCount,
      failureCount: result.failureCount,
      vendor,
    });

    return result;
  }

  async getAllVendors(): Promise<string[]> {
    const vendors = new Set<string>();
    let hasNextPage = true;
    let cursor: string | undefined;

    while (hasNextPage) {
      const response = await this.getProducts(250, cursor);

      response.products.edges.forEach(edge => {
        if (edge.node.vendor) {
          vendors.add(edge.node.vendor);
        }
      });

      hasNextPage = response.products.pageInfo.hasNextPage;
      cursor = response.products.pageInfo.endCursor;
    }

    return Array.from(vendors).sort();
  }

  async getProductsByVendor(vendor: string): Promise<ShopifyProduct[]> {
    const products: ShopifyProduct[] = [];
    let hasNextPage = true;
    let cursor: string | undefined;

    while (hasNextPage) {
      const response = await this.getProducts(250, cursor, `vendor:"${vendor}"`);

      response.products.edges.forEach(edge => {
        products.push(edge.node);
      });

      hasNextPage = response.products.pageInfo.hasNextPage;
      cursor = response.products.pageInfo.endCursor;
    }

    return products;
  }

  async validateConnection(): Promise<boolean> {
    try {
      await this.getProducts(1);
      return true;
    } catch (error) {
      logger.error('Shopify connection validation failed', {
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      return false;
    }
  }
}

export function createShopifyService(shopDomain: string, accessToken: string): ShopifyService {
  return new ShopifyService(shopDomain, accessToken);
}

server/utils/logger.ts.content:
interface LogLevel {
  ERROR: 'error';
  WARN: 'warn';
  INFO: 'info';
  DEBUG: 'debug';
}

const LOG_LEVELS: LogLevel = {
  ERROR: 'error',
  WARN: 'warn',
  INFO: 'info',
  DEBUG: 'debug',
};

interface LogEntry {
  timestamp: string;
  level: string;
  message: string;
  metadata?: any;
}

class Logger {
  private logLevel: string;

  constructor() {
    this.logLevel = process.env.LOG_LEVEL || 'info';
  }

  private shouldLog(level: string): boolean {
    const levels = ['error', 'warn', 'info', 'debug'];
    return levels.indexOf(level) <= levels.indexOf(this.logLevel);
  }

  private formatLog(level: string, message: string, metadata?: any): LogEntry {
    return {
      timestamp: new Date().toISOString(),
      level: level.toUpperCase(),
      message,
      ...(metadata && { metadata }),
    };
  }

  private output(logEntry: LogEntry): void {
    const logString = JSON.stringify(logEntry);

    if (logEntry.level === 'ERROR') {
      console.error(logString);
    } else if (logEntry.level === 'WARN') {
      console.warn(logString);
    } else {
      console.log(logString);
    }
  }

  error(message: string, metadata?: any): void {
    if (this.shouldLog(LOG_LEVELS.ERROR)) {
      this.output(this.formatLog(LOG_LEVELS.ERROR, message, metadata));
    }
  }

  warn(message: string, metadata?: any): void {
    if (this.shouldLog(LOG_LEVELS.WARN)) {
      this.output(this.formatLog(LOG_LEVELS.WARN, message, metadata));
    }
  }

  info(message: string, metadata?: any): void {
    if (this.shouldLog(LOG_LEVELS.INFO)) {
      this.output(this.formatLog(LOG_LEVELS.INFO, message, metadata));
    }
  }

  debug(message: string, metadata?: any): void {
    if (this.shouldLog(LOG_LEVELS.DEBUG)) {
      this.output(this.formatLog(LOG_LEVELS.DEBUG, message, metadata));
    }
  }

  // Specific methods for different types of logs
  apiCall(method: string, endpoint: string, statusCode: number, responseTime: number, metadata?: any): void {
    this.info('API Call', {
      method,
      endpoint,
      statusCode,
      responseTime,
      ...metadata,
    });
  }

  bulkJobStart(jobId: string, shopDomain: string, productCount: number): void {
    this.info('Bulk job started', {
      jobId,
      shopDomain,
      productCount,
      event: 'bulk_job_start',
    });
  }

  bulkJobComplete(jobId: string, processedCount: number, totalCount: number, duration: number): void {
    this.info('Bulk job completed', {
      jobId,
      processedCount,
      totalCount,
      duration,
      event: 'bulk_job_complete',
    });
  }

  bulkJobFailed(jobId: string, error: string, processedCount: number, totalCount: number): void {
    this.error('Bulk job failed', {
      jobId,
      error,
      processedCount,
      totalCount,
      event: 'bulk_job_failed',
    });
  }

  exportRequest(shopDomain: string, vendor?: string, filters?: any): void {
    this.info('Export requested', {
      shopDomain,
      vendor,
      filters,
      event: 'export_request',
    });
  }

  exportComplete(shopDomain: string, productCount: number, exportId: string): void {
    this.info('Export completed', {
      shopDomain,
      productCount,
      exportId,
      event: 'export_complete',
    });
  }

  shopifyApiError(shopDomain: string, endpoint: string, error: string, responseTime: number): void {
    this.error('Shopify API error', {
      shopDomain,
      endpoint,
      error,
      responseTime,
      event: 'shopify_api_error',
    });
  }

  shopifyRateLimit(shopDomain: string, retryAfter: number): void {
    this.warn('Shopify rate limit hit', {
      shopDomain,
      retryAfter,
      event: 'shopify_rate_limit',
    });
  }

  authSuccess(shopDomain: string, scope?: string): void {
    this.info('Shop authentication successful', {
      shopDomain,
      scope,
      event: 'auth_success',
    });
  }

  authFailure(shopDomain: string, error: string): void {
    this.error('Shop authentication failed', {
      shopDomain,
      error,
      event: 'auth_failure',
    });
  }

  cacheHit(key: string, type: string): void {
    this.debug('Cache hit', {
      key,
      type,
      event: 'cache_hit',
    });
  }

  cacheMiss(key: string, type: string): void {
    this.debug('Cache miss', {
      key,
      type,
      event: 'cache_miss',
    });
  }

  systemMetric(metricName: string, value: any, unit?: string): void {
    this.info('System metric', {
      metricName,
      value,
      unit,
      event: 'system_metric',
    });
  }
}

export const logger = new Logger();

// Helper function to measure execution time
export function measureTime<T>(
  operation: () => Promise<T>,
  operationName: string
): Promise<T> {
  const start = Date.now();

  return operation()
    .then(result => {
      const duration = Date.now() - start;
      logger.info(`Operation completed: ${operationName}`, { duration });
      return result;
    })
    .catch(error => {
      const duration = Date.now() - start;
      logger.error(`Operation failed: ${operationName}`, { 
        duration,
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      throw error;
    });
}

