client/src/pages/settings.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Header } from "@/components/layout/header";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Switch } from "@/components/ui/switch";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface ShopSettings {
  shopDomain: string;
  showVendorColumn: boolean;
  lastUpdated: string;
}

export default function Settings() {
  const [shopDomain, setShopDomain] = useState("");
  const [showVendorColumn, setShowVendorColumn] = useState(true);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: settings, isLoading } = useQuery<ShopSettings>({
    queryKey: ['/api/settings', shopDomain],
    enabled: !!shopDomain,
  });

  const updateSettingsMutation = useMutation({
    mutationFn: async (data: { shopDomain: string; showVendorColumn: boolean }) => {
      const response = await apiRequest("POST", "/api/settings", data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Settings updated",
        description: "Your shop settings have been saved successfully",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/settings'] });
    },
    onError: (error) => {
      toast({
        title: "Update failed",
        description: error instanceof Error ? error.message : "Unknown error",
        variant: "destructive",
      });
    },
  });

  // Update local state when settings data changes
  useEffect(() => {
    if (settings) {
      setShowVendorColumn(settings.showVendorColumn);
    }
  }, [settings]);

  const handleLoadSettings = () => {
    if (!shopDomain) {
      toast({
        title: "Shop domain required",
        description: "Please enter a shop domain to load settings",
        variant: "destructive",
      });
      return;
    }

    queryClient.invalidateQueries({ queryKey: ['/api/settings', shopDomain] });
  };

  const handleSaveSettings = () => {
    if (!shopDomain) {
      toast({
        title: "Shop domain required",
        description: "Please enter a shop domain to save settings",
        variant: "destructive",
      });
      return;
    }

    updateSettingsMutation.mutate({
      shopDomain,
      showVendorColumn,
    });
  };

  return (
    <div className="flex-1 flex flex-col overflow-hidden">
      <Header 
        title="Settings" 
        description="Configure shop preferences and vendor column display options"
      />

      <main className="flex-1 overflow-auto p-6 space-y-6">
        {/* Shop Configuration */}
        <Card>
          <CardHeader>
            <CardTitle>Shop Configuration</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-end space-x-4">
              <div className="flex-1">
                <Label htmlFor="shopDomain">Shop Domain</Label>
                <Input
                  id="shopDomain"
                  placeholder="example.myshopify.com"
                  value={shopDomain}
                  onChange={(e) => setShopDomain(e.target.value)}
                  data-testid="input-shop-domain"
                />
              </div>
              <Button 
                onClick={handleLoadSettings}
                disabled={isLoading || !shopDomain}
                data-testid="button-load-settings"
              >
                {isLoading ? "Loading..." : "Load Settings"}
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Vendor Column Settings */}
        <Card>
          <CardHeader>
            <CardTitle>Vendor Column Display</CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            {!shopDomain ? (
              <div className="text-center py-8 text-muted-foreground">
                Enter a shop domain to configure settings
              </div>
            ) : isLoading ? (
              <div className="space-y-4">
                <div className="animate-pulse flex items-center justify-between">
                  <div className="h-4 bg-muted rounded w-48"></div>
                  <div className="h-6 bg-muted rounded w-12"></div>
                </div>
              </div>
            ) : (
              <>
                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label htmlFor="showVendorColumn" className="text-base">
                      Show Vendor Column
                    </Label>
                    <div className="text-sm text-muted-foreground">
                      Display the vendor column in the product list interface
                    </div>
                  </div>
                  <Switch
                    id="showVendorColumn"
                    checked={showVendorColumn}
                    onCheckedChange={setShowVendorColumn}
                    data-testid="switch-show-vendor-column"
                  />
                </div>

                <div className="pt-4 border-t">
                  <Button 
                    onClick={handleSaveSettings}
                    disabled={updateSettingsMutation.isPending}
                    data-testid="button-save-settings"
                  >
                    {updateSettingsMutation.isPending ? "Saving..." : "Save Settings"}
                  </Button>
                </div>
              </>
            )}
          </CardContent>
        </Card>

        {/* Current Settings Display */}
        {settings && (
          <Card>
            <CardHeader>
              <CardTitle>Current Settings</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label className="text-sm text-muted-foreground">Shop Domain</Label>
                    <p className="font-medium" data-testid="current-shop-domain">{settings.shopDomain}</p>
                  </div>
                  <div>
                    <Label className="text-sm text-muted-foreground">Vendor Column Visible</Label>
                    <p className="font-medium" data-testid="current-vendor-column-status">
                      {settings.showVendorColumn ? "Yes" : "No"}
                    </p>
                  </div>
                  <div>
                    <Label className="text-sm text-muted-foreground">Last Updated</Label>
                    <p className="font-medium" data-testid="current-last-updated">
                      {new Date(settings.lastUpdated).toLocaleString()}
                    </p>
                  </div>
                  <div>
                    <Label className="text-sm text-muted-foreground">Status</Label>
                    <p className="font-medium text-green-600" data-testid="current-settings-status">
                      <i className="fas fa-check-circle mr-1"></i>
                      Active
                    </p>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Settings Information */}
        <Card>
          <CardHeader>
            <CardTitle>About Settings</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3 text-sm text-muted-foreground">
              <p>
                <strong>Vendor Column Display:</strong> Controls whether the vendor column is visible in your Shopify admin product list. When enabled, you can see and manage vendor information directly from the product interface.
              </p>
              <p>
                <strong>Configuration Persistence:</strong> All settings are automatically saved and will persist across sessions. Changes take effect immediately in your Shopify admin interface.
              </p>
              <p>
                <strong>Bulk Operations:</strong> When the vendor column is enabled, you can perform bulk vendor updates and exports directly from the product list interface.
              </p>
            </div>
          </CardContent>
        </Card>
      </main>
    </div>
  );
}

client/src/pages/vendors.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Header } from "@/components/layout/header";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useVendors } from "@/hooks/use-vendors";

export default function Vendors() {
  const [shopDomain, setShopDomain] = useState("");
  const [exportVendor, setExportVendor] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { vendors, isLoading: vendorsLoading, refreshVendors } = useVendors(shopDomain);

  const exportMutation = useMutation({
    mutationFn: async (data: { shopDomain: string; vendor?: string }) => {
      const response = await apiRequest("POST", "/api/export", data);
      return response.json();
    },
    onSuccess: (data) => {
      // Open download URL in new tab
      window.open(data.downloadUrl, '_blank');
      toast({
        title: "Export created",
        description: "Your CSV export is ready for download",
      });
    },
    onError: (error) => {
      toast({
        title: "Export failed",
        description: error instanceof Error ? error.message : "Unknown error",
        variant: "destructive",
      });
    },
  });

  const handleLoadVendors = () => {
    if (!shopDomain) {
      toast({
        title: "Shop domain required",
        description: "Please enter a shop domain to load vendors",
        variant: "destructive",
      });
      return;
    }
    refreshVendors();
  };

  const handleExport = (vendor?: string) => {
    if (!shopDomain) {
      toast({
        title: "Shop domain required",
        description: "Please enter a shop domain to export",
        variant: "destructive",
      });
      return;
    }

    exportMutation.mutate({
      shopDomain,
      vendor: vendor || undefined,
    });
  };

  const handleExportAll = () => {
    handleExport();
  };

  const handleExportByVendor = () => {
    if (!exportVendor) {
      toast({
        title: "Vendor required",
        description: "Please select a vendor to export",
        variant: "destructive",
      });
      return;
    }
    handleExport(exportVendor);
  };

  return (
    <div className="flex-1 flex flex-col overflow-hidden">
      <Header 
        title="Vendors" 
        description="Manage vendor lists and export product data by vendor"
      />

      <main className="flex-1 overflow-auto p-6 space-y-6">
        {/* Shop Domain Input */}
        <Card>
          <CardHeader>
            <CardTitle>Shop Configuration</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-end space-x-4">
              <div className="flex-1">
                <Label htmlFor="shopDomain">Shop Domain</Label>
                <Input
                  id="shopDomain"
                  placeholder="example.myshopify.com"
                  value={shopDomain}
                  onChange={(e) => setShopDomain(e.target.value)}
                  data-testid="input-shop-domain"
                />
              </div>
              <Button 
                onClick={handleLoadVendors}
                disabled={vendorsLoading || !shopDomain}
                data-testid="button-load-vendors"
              >
                {vendorsLoading ? "Loading..." : "Load Vendors"}
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Vendor List */}
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle>Vendor List</CardTitle>
              <div className="flex space-x-2">
                <Button
                  variant="outline"
                  onClick={handleLoadVendors}
                  disabled={vendorsLoading || !shopDomain}
                  data-testid="button-refresh-vendors"
                >
                  <i className="fas fa-sync mr-2"></i>
                  Refresh
                </Button>
                <Button
                  onClick={handleExportAll}
                  disabled={exportMutation.isPending || !shopDomain}
                  data-testid="button-export-all"
                >
                  <i className="fas fa-download mr-2"></i>
                  Export All Products
                </Button>
              </div>
            </div>
          </CardHeader>
          <CardContent>
            {!shopDomain ? (
              <div className="text-center py-8 text-muted-foreground">
                Enter a shop domain to load vendors
              </div>
            ) : vendorsLoading ? (
              <div className="space-y-3">
                {[...Array(6)].map((_, i) => (
                  <div key={i} className="animate-pulse flex items-center justify-between p-3 border rounded">
                    <div className="h-4 bg-muted rounded w-32"></div>
                    <div className="h-6 bg-muted rounded w-16"></div>
                  </div>
                ))}
              </div>
            ) : vendors.length === 0 ? (
              <div className="text-center py-8 text-muted-foreground">
                No vendors found for this shop
              </div>
            ) : (
              <div className="space-y-2">
                {vendors.map((vendor, index) => (
                  <div 
                    key={index} 
                    className="flex items-center justify-between p-3 border rounded hover:bg-muted/50"
                    data-testid={`vendor-item-${index}`}
                  >
                    <div className="flex items-center space-x-3">
                      <i className="fas fa-building text-muted-foreground"></i>
                      <span className="font-medium">{vendor}</span>
                    </div>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleExport(vendor)}
                      disabled={exportMutation.isPending}
                      data-testid={`button-export-vendor-${index}`}
                    >
                      <i className="fas fa-download mr-1"></i>
                      Export
                    </Button>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>

        {/* Export by Vendor */}
        <Card>
          <CardHeader>
            <CardTitle>Export by Specific Vendor</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-end space-x-4">
              <div className="flex-1">
                <Label htmlFor="exportVendor">Vendor Name</Label>
                <Input
                  id="exportVendor"
                  placeholder="Enter vendor name..."
                  value={exportVendor}
                  onChange={(e) => setExportVendor(e.target.value)}
                  data-testid="input-export-vendor"
                />
              </div>
              <Button 
                onClick={handleExportByVendor}
                disabled={exportMutation.isPending || !shopDomain || !exportVendor}
                data-testid="button-export-by-vendor"
              >
                {exportMutation.isPending ? "Exporting..." : "Export Vendor Products"}
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Vendor Statistics */}
        {vendors.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>Vendor Statistics</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div className="text-center p-4 border rounded">
                  <div className="text-2xl font-bold text-foreground" data-testid="stat-total-vendors">
                    {vendors.length}
                  </div>
                  <div className="text-sm text-muted-foreground">Total Vendors</div>
                </div>
                <div className="text-center p-4 border rounded">
                  <div className="text-2xl font-bold text-foreground">
                    <i className="fas fa-building text-primary"></i>
                  </div>
                  <div className="text-sm text-muted-foreground">Vendor Data Loaded</div>
                </div>
                <div className="text-center p-4 border rounded">
                  <div className="text-2xl font-bold text-foreground">
                    <i className="fas fa-sync text-green-600"></i>
                  </div>
                  <div className="text-sm text-muted-foreground">Cache Updated</div>
                </div>
              </div>
            </CardContent>
          </Card>
        )}
      </main>
    </div>
  );
}

client/src/components/dashboard/api-stat.content:
import { useQuery } from "@tanstack/react-query";

interface ApiEndpointStats {
  endpoint: string;
  avgResponseTime: number;
  errorRate: number;
}

export function ApiStatusPanel() {
  const { data, isLoading } = useQuery<{ 
    activeShops: number;
    totalVendors: number;
    bulkJobsToday: number;
    apiSuccessRate: number;
    apiEndpoints: ApiEndpointStats[];
  }>({
    queryKey: ['/api/stats'],
  });

  if (isLoading) {
    return (
      <div className="bg-card rounded-lg border border-border">
        <div className="p-6 border-b border-border">
          <h3 className="text-lg font-semibold text-foreground">API Endpoints</h3>
          <p className="text-sm text-muted-foreground">Real-time status monitoring</p>
        </div>
        <div className="p-6 space-y-4">
          {[...Array(4)].map((_, i) => (
            <div key={i} className="animate-pulse flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <div className="w-2 h-2 bg-muted rounded-full"></div>
                <div className="space-y-1">
                  <div className="h-4 bg-muted rounded w-32"></div>
                  <div className="h-3 bg-muted rounded w-16"></div>
                </div>
              </div>
              <div className="h-4 bg-muted rounded w-16"></div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  const getStatusColor = (avgResponseTime: number, errorRate: number) => {
    if (errorRate > 5 || avgResponseTime > 1000) return "bg-red-500";
    if (errorRate > 1 || avgResponseTime > 500) return "bg-orange-500";
    return "bg-green-500";
  };

  const getStatusText = (avgResponseTime: number, errorRate: number) => {
    if (errorRate > 5 || avgResponseTime > 1000) return { text: "Unhealthy", color: "text-red-600" };
    if (errorRate > 1 || avgResponseTime > 500) return { text: "Slow", color: "text-orange-600" };
    return { text: "Healthy", color: "text-green-600" };
  };

  // Default endpoints if no data
  const defaultEndpoints = [
    { endpoint: "/api/vendors", avgResponseTime: 127, errorRate: 0 },
    { endpoint: "/api/bulk-update-vendor", avgResponseTime: 234, errorRate: 0 },
    { endpoint: "/api/export", avgResponseTime: 1200, errorRate: 1.2 },
    { endpoint: "/api/settings", avgResponseTime: 89, errorRate: 0 },
  ];

  const endpoints = data?.apiEndpoints?.length ? data.apiEndpoints : defaultEndpoints;

  return (
    <div className="bg-card rounded-lg border border-border">
      <div className="p-6 border-b border-border">
        <h3 className="text-lg font-semibold text-foreground">API Endpoints</h3>
        <p className="text-sm text-muted-foreground">Real-time status monitoring</p>
      </div>
      <div className="p-6 space-y-4">
        {endpoints.map((endpoint, index) => {
          const statusColor = getStatusColor(endpoint.avgResponseTime, endpoint.errorRate);
          const status = getStatusText(endpoint.avgResponseTime, endpoint.errorRate);

          return (
            <div key={index} className="flex items-center justify-between" data-testid={`endpoint-${endpoint.endpoint.replace(/[^a-zA-Z0-9]/g, '-')}`}>
              <div className="flex items-center space-x-3">
                <div className={`w-2 h-2 ${statusColor} rounded-full`}></div>
                <div>
                  <p className="text-sm font-medium text-foreground">{endpoint.endpoint}</p>
                  <p className="text-xs text-muted-foreground">{endpoint.avgResponseTime}ms avg</p>
                </div>
              </div>
              <span className={`text-sm font-medium ${status.color}`}>{status.text}</span>
            </div>
          );
        })}
      </div>
    </div>
  );
}

client/src/components/dashboard/bulk-job.content:
import { useQuery } from "@tanstack/react-query";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import type { BulkJob } from "@shared/schema";

export function BulkJobsTable() {
  const { data, isLoading } = useQuery<{ jobs: BulkJob[] }>({
    queryKey: ['/api/bulk-jobs'],
  });

  if (isLoading) {
    return (
      <div className="lg:col-span-2 bg-card rounded-lg border border-border">
        <div className="p-6 border-b border-border">
          <h3 className="text-lg font-semibold text-foreground">Recent Bulk Jobs</h3>
        </div>
        <div className="p-6 space-y-4">
          {[...Array(3)].map((_, i) => (
            <div key={i} className="animate-pulse flex space-x-4">
              <div className="h-4 bg-muted rounded flex-1"></div>
              <div className="h-4 bg-muted rounded w-20"></div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  const getStatusBadge = (status: BulkJob['status']) => {
    const statusStyles = {
      SUCCESS: "bg-green-100 text-green-800",
      RUNNING: "bg-orange-100 text-orange-800",
      PENDING: "bg-blue-100 text-blue-800",
      FAILED: "bg-red-100 text-red-800"
    };

    if (!status) return <Badge>Unknown</Badge>;

    return (
      <Badge className={`px-2 py-1 text-xs font-medium rounded-full ${statusStyles[status]}`}>
        {status}
      </Badge>
    );
  };

  const formatTimeAgo = (date: Date | string) => {
    const now = new Date();
    const then = new Date(date);
    const diffMs = now.getTime() - then.getTime();
    const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
    const diffMinutes = Math.floor(diffMs / (1000 * 60));

    if (diffHours > 0) {
      return `${diffHours} hours ago`;
    } else if (diffMinutes > 0) {
      return `${diffMinutes} min ago`;
    } else {
      return 'Just now';
    }
  };

  return (
    <div className="lg:col-span-2 bg-card rounded-lg border border-border">
      <div className="p-6 border-b border-border">
        <div className="flex items-center justify-between">
          <h3 className="text-lg font-semibold text-foreground">Recent Bulk Jobs</h3>
          <Button variant="ghost" size="sm" className="text-primary hover:text-primary/80">
            View All
          </Button>
        </div>
      </div>
      <div className="overflow-x-auto">
        <table className="w-full">
          <thead className="bg-muted">
            <tr>
              <th className="text-left p-4 text-sm font-medium text-muted-foreground">Job ID</th>
              <th className="text-left p-4 text-sm font-medium text-muted-foreground">Shop</th>
              <th className="text-left p-4 text-sm font-medium text-muted-foreground">Products</th>
              <th className="text-left p-4 text-sm font-medium text-muted-foreground">Target Vendor</th>
              <th className="text-left p-4 text-sm font-medium text-muted-foreground">Status</th>
              <th className="text-left p-4 text-sm font-medium text-muted-foreground">Created</th>
            </tr>
          </thead>
          <tbody>
            {data?.jobs?.length === 0 ? (
              <tr>
                <td colSpan={6} className="p-8 text-center text-muted-foreground">
                  No bulk jobs found
                </td>
              </tr>
            ) : (
              data?.jobs?.map((job) => (
                <tr key={job.id} className="border-b border-border hover:bg-muted/50" data-testid={`job-row-${job.id}`}>
                  <td className="p-4 text-sm font-mono text-foreground">#{job.id.slice(0, 12)}</td>
                  <td className="p-4 text-sm text-foreground">{job.shopDomain}</td>
                  <td className="p-4 text-sm text-foreground">{job.totalCount}</td>
                  <td className="p-4 text-sm text-foreground">{job.targetVendor}</td>
                  <td className="p-4">
                    {getStatusBadge(job.status)}
                  </td>
                  <td className="p-4 text-sm text-muted-foreground">{formatTimeAgo(job.createdAt!)}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

client/src/components/dashboard/quick-ac.content:
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

export function QuickActions() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const refreshCacheMutation = useMutation({
    mutationFn: async () => {
      // Invalidate vendors cache to force refresh
      queryClient.invalidateQueries({ queryKey: ['/api/vendors'] });
      return { success: true };
    },
    onSuccess: () => {
      toast({
        title: "Cache refreshed",
        description: "Vendor lists have been updated",
      });
    },
    onError: () => {
      toast({
        title: "Refresh failed",
        description: "Could not refresh vendor cache",
        variant: "destructive",
      });
    },
  });

  const exportLogsMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("GET", "/api/logs?limit=1000");
      return response.json();
    },
    onSuccess: (data) => {
      // Create and download CSV
      const csvContent = convertLogsToCSV(data.logs);
      downloadCSV(csvContent, "system-logs.csv");

      toast({
        title: "Logs exported",
        description: "System logs have been downloaded",
      });
    },
    onError: () => {
      toast({
        title: "Export failed",
        description: "Could not export system logs",
        variant: "destructive",
      });
    },
  });

  const healthCheckMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("GET", "/api/health");
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Health check complete",
        description: `System status: ${data.status}`,
      });
    },
    onError: () => {
      toast({
        title: "Health check failed",
        description: "System diagnostics failed",
        variant: "destructive",
      });
    },
  });

  const convertLogsToCSV = (logs: any[]) => {
    if (!logs || logs.length === 0) return "";

    const headers = ["Timestamp", "Endpoint", "Method", "Status Code", "Response Time", "Shop Domain"];
    const rows = logs.map(log => [
      log.timestamp,
      log.endpoint,
      log.method,
      log.statusCode,
      log.responseTime,
      log.shopDomain || ""
    ]);

    return [headers.join(","), ...rows.map(row => row.join(","))].join("\n");
  };

  const downloadCSV = (content: string, filename: string) => {
    const blob = new Blob([content], { type: "text/csv" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);
  };

  const actions = [
    {
      title: "Refresh Cache",
      description: "Update vendor lists",
      icon: "fas fa-sync",
      bgColor: "bg-primary/10",
      iconColor: "text-primary",
      onClick: () => refreshCacheMutation.mutate(),
      isLoading: refreshCacheMutation.isPending,
      testId: "action-refresh-cache"
    },
    {
      title: "Export Logs",
      description: "Download system logs",
      icon: "fas fa-download",
      bgColor: "bg-secondary",
      iconColor: "text-secondary-foreground",
      onClick: () => exportLogsMutation.mutate(),
      isLoading: exportLogsMutation.isPending,
      testId: "action-export-logs"
    },
    {
      title: "Health Check",
      description: "Run diagnostics",
      icon: "fas fa-heartbeat",
      bgColor: "bg-green-100",
      iconColor: "text-green-600",
      onClick: () => healthCheckMutation.mutate(),
      isLoading: healthCheckMutation.isPending,
      testId: "action-health-check"
    },
    {
      title: "New Bulk Job",
      description: "Start vendor update",
      icon: "fas fa-magic",
      bgColor: "bg-accent",
      iconColor: "text-accent-foreground",
      onClick: () => {
        // Navigate to bulk jobs page
        window.location.href = "/bulk-jobs";
      },
      isLoading: false,
      testId: "action-new-bulk-job"
    }
  ];

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
      {actions.map((action, index) => (
        <Button
          key={index}
          variant="ghost"
          className="bg-card border border-border rounded-lg p-6 text-left hover:shadow-md transition-shadow h-auto"
          onClick={action.onClick}
          disabled={action.isLoading}
          data-testid={action.testId}
        >
          <div className="flex items-center space-x-3">
            <div className={`w-10 h-10 ${action.bgColor} rounded-lg flex items-center justify-center`}>
              <i className={`${action.icon} ${action.iconColor}`}></i>
            </div>
            <div>
              <h4 className="font-medium text-foreground">{action.title}</h4>
